import React, { useState } from "react";
import Header from "./Header";
import Footer from "./Footer";
import Note from "./Note";
import CreateArea from "./CreateArea";

function App() {

  const[notes,setNotes]=useState([]);

  function addNote(newNote){
    setNotes(prevNote => {
      return  [...prevNote,newNote];

    })
  };

  function deleteNote(id){
       setNotes(prevNotes => {
        return  prevNotes.filter((noteItem,index) => {
           return index !==id;
         });
       });
  }
  return (
    <div>
      <Header />
      <CreateArea  onAdd={addNote}//onAdd burda propsu create araeanın içinde cegırıyoruz onu

       />
       {notes && notes.map((noteItems,index) => { //notes && ekledik çunku aray sılıdıgınden delete dediğinde donmuyor
           return( <Note 
           key={index}
           id={index}
           title={noteItems.title} 
           content={noteItems.content} 
           onDelete={deleteNote} />);
       })}

      <Footer />
    </div>
  );
}

export default App;
